namespace FEZEngine.Structure
{
    public class TrileEmplacement
    {
        public int X;
        public int Y;
        public int Z;

        public TrileEmplacement() {}

        public TrileEmplacement(int x, int y, int z)
        {
            X = x;
            Y = y;
            Z = z;
        }
    }
}
